mapper.insert:
INSERT INTO tb_notice (
	 writer,
	 title, 
	 content, 
	 img_saved, 
	 img_original, 
	 insert_date, 
	 modified_date, 
	 division,
	 notice_yn,
	 secret_yn,
	 create_ip,
	 modify_ip
) VALUES (
	  #{writer}
	, #{title}
	, #{content}
	, #{imgSaved}
	, #{imgOriginal}
	, NOW()
	, NOW()		
	, #{division}
	, IFNULL(#{noticeYn}, 'N')
	, IFNULL(#{secretYn}, 'N')
	, #{createIp}			
	, #{modifyIp}
)

mapper.update:
	    UPDATE tb_notice
    SET
        notice_yn = #{noticeYn},
        secret_yn = #{secretYn}
    WHERE id = #{id}
